import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import DataTable from 'react-data-table-component';
import { CSVLink} from "react-csv";
import differenceBy from 'lodash/differenceBy';

//"react-data-table-component": "^7.5.2",   ES EL QUE FUNCIONA
//
//"datatables.net": "^1.12.1",      NO SE IMPLEMENTA, NO INSTALAR EN OTROS PROYECTOS
//"datatables.net-dt": "^1.12.1",   NO SE IMPLEMENTA, NO INSTALAR EN OTROS PROYECTOS

const ListPersona = () => {
  const [persona, setPersona] = useState([]);
  const [searchTerm, setSearch ] = useState("")
  const [selectedRows, setSelectedRows] = useState([]);
  const [toggleCleared, setToggleCleared] = useState(false);
  let itemexport = 0;
  /*const [personaSearch, setPersonaSearch] = useState([]);*/
  //let[currentPage, setCurrentPage] = useState(0);
  //const [ search, setSearch ] = useState("")
	
  //let i;

  useEffect(() => {
    getPersona();
   // getPersonaSearch();
  }, []);
/*
  /*PAGINACION ECHA POR LOS CHICOS /* 
  const searcher = (e) => {
    setSearch(e.target.value)   
  }

    const nextPage = () => {
    if(currentPage<=persona.length)
    currentPage =currentPage+i;
    setCurrentPage (currentPage);
    getPersona(currentPage)
    console.log(currentPage)
  } 

  const prevPage = () => {
   if(currentPage>0)
    currentPage =currentPage-i;
    setCurrentPage (currentPage);
    getPersona(currentPage)
    console.log(currentPage)
  }

  const getPersonaSearch = async () => {
    const response = await axios.get("http://localhost:5000/persona");
    setPersonaSearch(response.data);
  };
*/

/*
   const results = !search ? personaSearch : personaSearch.filter((dato)=> dato.nombre.toLowerCase().includes(search.toLocaleLowerCase()))
   results.slice(currentPage, currentPage+i);
*/

const getPersona = async () => {
  const response = await axios.get("http://localhost:5000/persona");
  //setPersona(response.data.slice(currentPage, currentPage+i));
  setPersona(response.data);
};

const columnas = [
  {
    name : 'idPersona',
    selector : 'idPersona',
    sortable : true
  },
  {
    name : 'email',
    selector : 'email',
    sortable : true
  },
  {
    name : 'nombre',
    selector : 'nombre',
    sortable : true
  },
  {
    name : 'apellido',
    selector : 'apellido',
    sortable : true
  },
  {
    name : 'edad',
    selector : 'edad',
    sortable : true
  },
  {
    name : 'sexo',
    selector : 'sexo',
    sortable : true
  },
  {
    cell: (persona) => <Link to={`edit/${persona.idPersona}`}
    className="button is-small is-info mr-2 icon-pencil"
    title="Editar">Editar</Link>,
    ignoreRowClick: true,
    allowOverflow: true,
    button: true,

  }
  
]

const searcher = (e) => {
  setSearch(e.target.value)   
}




	const handleRowSelected = React.useCallback(state => {
		setSelectedRows(selectedRows);
	}, []);

	const contextActions = React.useMemo(() => {
		const handleDelete = () => {
			
			if ( window.confirm(`Are you sure you want to delete:\r ${selectedRows.map(r => r.title)}?`)) {
				setToggleCleared(!toggleCleared);
				setPersona(differenceBy(persona, selectedRows, 'title'));
			}
		};

		return (
			<input key="delete" onClick={handleDelete} style={{ backgroundColor: 'red' }} icon>
				Delete
			</input>
		);
	}, [persona, selectedRows, toggleCleared]);

  return (
    /*PAGINACION DE FRAME DATATABLE*/        
<body>
    <div>

    <input type="text" name="searchTerm" id="searchTerm" onChange={searcher}></input>
    <Link to={`add`} className="button is-success">
    Nuevo</Link>
    <CSVLink data={persona} className="button is-success" filename={"VEC2_personas.csv"}>Exportar campos seleccionados CSV</CSVLink>
      <DataTable
      pagination
      selectableRows
      contextActions={contextActions}
			onSelectedRowsChange={handleRowSelected}
			clearSelectedRows={toggleCleared}
      columns={columnas}
      data={persona.filter((item) => {
        if (searchTerm === "") {
          return item;
        } else if (
          item.nombre.toLowerCase().includes(searchTerm.toLowerCase()) || item.email.toLowerCase().includes(searchTerm.toLowerCase()) || item.apellido.toLowerCase().includes(searchTerm.toLowerCase())
        ) {
          itemexport = item;
          return item;
        }
      })} 
       />
    </div>
</body>

  );
};


export default ListPersona;
