import React, { useState, useEffect} from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
 
const AddUser = () => {
  //
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [perfil, setGetPerfil] = useState([]);
  const [id_perfil, setPerfil] = useState("");

  const navigate = useNavigate();

  const perfiles_por_usuario = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/perfiles_por_usuario", {
        name,
        email,
        id_perfil,
        //id_user,
      });
      navigate("/");
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {

    getPerfil();
  }, []);

  const getPerfil = async () => {
    const response = await axios.get("http://localhost:5000/perfiles");
    setGetPerfil(response.data);  
  };

  return (
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
      <form onSubmit={(event) => {/*saveUser(event);*//*getLastId(event);*/perfiles_por_usuario(event);}}>  {/*para poder cargar los datos de user tambien hay que encontrar la forma de agregar ambos metodos en el mismo form*/}
          <div className="field">
            <label className="label">Name</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="Name"
                required
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Email</label>
            <div className="control">
              <input
                type="email"
                className="input"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Email"
                required
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Perfiles</label>
            <div className="control">
              <div className="select is-fullwidth">
                <select
                    value={id_perfil}                       
                    onChange={(e) => setPerfil(e.target.value)}
                    key={perfil.id}
                >
                <option value="0">Seleccionar</option>
                  {perfil.map((perfil) => (
                  
                  <option value={perfil.id_perfil}>{perfil.nombre_perfil}</option>
                
                  ))}
                </select>
              </div>
            </div>
          </div>
          

          <div className="field">
            <button type="submit" className="button is-success">
              Save
            </button>
            <Link
                    to={'/'}
                    className="button is-small is-info mr-2"
                  >
                    Back
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddUser;
