import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const ListPersona = () => {
  const [persona, setPersona] = useState([]);
  const[currentPage, setCurrentPage] = useState(0);
  const [ search, setSearch ] = useState('');

  useEffect(() => {
    getPersona();
  }, []);

  const getPersona = async () => {
    const response = await axios.get("http://localhost:5000/persona");
    setPersona(response.data);
  };
  
  const filteredPersona = () => {
    {persona.map((persona))}
    if( search.length === 0 ) 
        return persona.slice(currentPage, currentPage + 5);
    const filtered = persona.filter( poke => poke.name.includes( search ) );
    return filtered.slice( currentPage, currentPage + 5);
}

const nextPage = () => {
  if ( persona.filter( persona => persona.name.includes( search ) ).length > currentPage + 5 )
      setCurrentPage( currentPage + 5 );
}

const prevPage = () => {
  if ( currentPage > 0 )
      setCurrentPage( currentPage - 5 );
}
/*
const onSearchChange = ({ target }: ChangeEvent<
  HTMLInputElement>) => {
  setCurrentPage(0);
  setSearch( target.value );
}
*/

  return (
    <body>
    <header>
      <div className="div-img-center">
    <img className="img scroll is-variable" src="https://ine.gov.ar/vigilancia/wp-content/uploads/2021/02/VEC-COVD-300x300.png"></img>
    </div>
    </header>
    <div className="">
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
        <Link to={`add`} className="button is-success">
          Add Persona
        </Link>

        <button 
                className="btn btn-primary"
                onClick={ prevPage }
            >
                Anteriores
            </button>
            &nbsp;
            <button 
                className="btn btn-primary"
                onClick={ nextPage }
            >
                Siguientes
            </button>

        <table className="table is-striped is-fullwidth">
          <thead>
            <tr>
              <th>No</th>
              <th>email</th>
              <th>nombre</th>
              <th>apellido</th>
              <th>edad</th>
              <th>sexo</th>
              <th>actions</th>
            </tr>
          </thead>
          <tbody>
          {
                        filteredPersona().map( ({ idPersona, email, nombre, apellido, edad, sexo }) => (
                            <tr key={ idPersona }>
                                <td> { idPersona } </td>
                                <td> { email } </td>
                                <td> { nombre } </td>
                                <td> { apellido } </td>
                                <td> { edad } </td>
                                <td> { sexo } </td>
                                <td>
                <Link
                    to={`edit/${persona.idPersona}`}
                    
                    className="button is-small is-info mr-2 icon-pencil"
                    title="Editar"
                  >
                    edit
                  </Link>
                </td>
                            </tr>
                        ))
                    }
            
          </tbody>
        </table>
    </div>
    </div>
    </div>
    </body>
  );
};

export default ListPersona;
