import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams, Link} from "react-router-dom";

const EditPersona = () => {

  const [email, setEmail] = useState("");
  const [nombre, setNombre] = useState("");
  const [apellido, setApellido] = useState("");
  const [edad, setEdad] = useState("");
  const [sexo, setSexo] = useState("");
  const { idPersona } = useParams();
  const navigate = useNavigate();


  useEffect(() => {
    getPersonaById();
  }, []);

  const updatePersona= async (e) => {
    e.preventDefault();
    try {
      await axios.patch(`http://localhost:5000/persona/${idPersona}`, {
        email,
        nombre,
        apellido,
        edad,
        sexo,
      });
      navigate("/");
    } catch (error) {
      console.log(error);
    }
  };

  const getPersonaById = async () => {
    const response = await axios.get(`http://localhost:5000/persona/${idPersona}`);
    const i = 0;
    setEmail(response.data[i].email);
    setNombre(response.data[i].nombre);
    setApellido(response.data[i].apellido);
    setEdad(response.data[i].edad);
    setSexo(response.data[i].sexo);
    console.log(JSON.stringify(response, null, 1));

  };

  const volverPerfilList = "to={'/'}";

  return (
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
        <form onSubmit={updatePersona}>
          <div className="field">
            <label className="label">email</label>
            <div className="control">
              <input
                type="email"
                className="input"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="email"
                required
              />
            </div>
          </div>
          <div className="field">
            <label className="label">nombre</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={nombre}
                onChange={(e) => setNombre(e.target.value)}
                placeholder="nombre"
                required
              />
            </div>
          </div>
          <div className="field">
            <label className="label">apellido</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={apellido}
                onChange={(e) => setApellido(e.target.value)}
                placeholder="apellido"
                required
              />
            </div>
          </div>
          <div className="field">
            <label className="label">edad</label>
            <div className="control">
              <input
                type="number"
                className="input"
                value={edad}
                onChange={(e) => setEdad(e.target.value)}
                placeholder="edad"
                required
              />
            </div>
          </div>
          <div className="field">
            <label className="label">sexo</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={sexo}
                onChange={(e) => setSexo(e.target.value)}
                placeholder="sexo"
                required
              />
            </div>
          </div>
          <div className="field">
            <button type="submit" className="button is-success">
              Update
            </button>
            <Link
                    to={'/'}
                    className="button is-small is-info mr-2"
                  >
                    Back2
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditPersona;
