import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";


const ListPersona = () => {
  const [persona, setPersona] = useState([]);
  let[currentPage, setCurrentPage] = useState(0);
  const [busqueda, setBusqueda]= useState("");
  let i;

  useEffect(() => {
    getPersona();
  }, []);

  const nextPage = () => {
    if(currentPage<=persona.length)
    currentPage =currentPage+i;
    setCurrentPage (currentPage);
    getPersona(currentPage)
    console.log(currentPage)
  } 

  const prevPage = () => {
   if(currentPage>0)
    currentPage =currentPage-i;
    setCurrentPage (currentPage);
    getPersona(currentPage)
    console.log(currentPage)
  }

  const handleChange= (e) =>{
    setBusqueda(e.target.value);
    filtrar(e.target.value);
  }

  const filtrar=(terminoBusqueda)=>{
    var resultadosBusqueda=persona.filter((elemento)=>{
      if(elemento.email.toString().toLowerCase().includes(terminoBusqueda.toLowerCase())
      || elemento.nombre.toString().toLowerCase().includes(terminoBusqueda.toLowerCase())
      ){
        return elemento;
      }
    });
    setPersona(resultadosBusqueda);
  }

  const getPersona = async () => {
     const response = await axios.get("http://localhost:5000/persona");
     setPersona(response.data.slice(currentPage, currentPage+i));
   };

  return (
    <body>
    <header>
      <div className="div-img-center">
    <img className="img scroll is-variable" src="https://ine.gov.ar/vigilancia/wp-content/uploads/2021/02/VEC-COVD-300x300.png"></img>
    </div>
    </header>
    <div className="">
    <div className="columns mt-5 is-centered">

      <div className="column is-half">
        <Link to={`add`} className="button is-success">
          Add Persona
        </Link>

      <div className="containerInput">
        <input
          className="form-control inputBuscar"
          value={busqueda}
          placeholder="Búsqueda por Nombre o Empresa"
          onChange={handleChange}
        />
        {/*<input type="submit">enviar</input>*/}
      </div>

        <button 
                className="btn btn-primary"
                onClick={ prevPage }
            >
                Anteriores
            </button>
            &nbsp;
            <button 
                className="btn btn-primary"
                onClick={ nextPage }
            >
                Siguientes
            </button>
          
          
<select onChange={getPersona}>
  <option value={i=5}>5</option>
</select>

        <table className="table is-striped is-fullwidth">
          <thead>
            <tr>
              <th>No</th>
              <th>email</th>
              <th>nombre</th>
              <th>apellido</th>
              <th>edad</th>
              <th>sexo</th>
              <th>actions</th>
            </tr>
          </thead>
          <tbody>
            {persona.map((persona) => (
              <tr key={persona.idPersona}>
                <td>{persona.idPersona}</td>
                <td>{persona.email}</td>
                <td>{persona.nombre}</td>
                <td>{persona.apellido}</td>                
                <td>{persona.edad}</td>
                <td>{persona.sexo}</td>
                <td>
                <Link
                    to={`edit/${persona.idPersona}`}
                    
                    className="button is-small is-info mr-2 icon-pencil"
                    title="Editar"
                  >
                    edit
                  </Link>
                </td>
              </tr>
            ))}
            
          </tbody>
        </table>
    </div>
    </div>
    </div>
    </body>
  );
};

export default ListPersona;
