import React, { useState, useEffect} from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
 
const SeleccionarVisitaGuiada = () => {
  //
  const [NombreVisitante, setNombreVisitante] = useState("");
  const [Apellido, setApellido] = useState("");
  const [Email, setEmail] = useState("");
  const [Telefono, setTelefono] = useState("");
  const [Documento, setDocumento] = useState("");
  const [IdVisitaGuiada, setIdVisitaGuiada] = useState("");
  const [VisitasGuiadas, setVisitasGuiadas]= useState([]);
  const [IdIdioma, setIdIdioma]= useState("");
  const [Idiomas, setIdiomas]= useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    getVisitasGuiadas();
    getIdiomas();
    getVisitasGuiadasPorIdioma();
  }, []);

  const SeleccionarVisitaGuiada = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/visitante", {
      NombreVisitante,  
      Apellido,
      Email,
      Telefono,
      Documento,
      IdVisitaGuiada
      });
      navigate("/listarArea");
    } catch (error) {
      console.log(error);
    }
  };

  const getVisitasGuiadas = async () => {
    const response = await axios.get("http://localhost:5000/visitasGuiadas");
    setVisitasGuiadas(response.data);
  };

  const getIdiomas = async () => {
    const response = await axios.get("http://localhost:5000/idiomas");
    setIdiomas(response.data);
  };

  const getVisitasGuiadasPorIdioma = async () => {
    const response = await axios.get(`http://localhost:5000/obras/${IdIdioma}`);
    const i = 0;
    
    setAutor(response.data[i].Autor);
    setDescripcion(response.data[i].Descripcion);
    setNombre(response.data[i].Nombre);
    setIdTipoObra(response.data[i].IdTipoObra);
    console.log(JSON.stringify(response, null, 1));

  };

  return (
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
      <form onSubmit={(event) => {SeleccionarVisitaGuiada(event);}}>
          <div className="field">
            <label className="label">Nombre</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={NombreVisitante}
                onChange={(e) => setNombreVisitante(e.target.value)}
                placeholder="Nombre"
                required
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Apellido</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={Apellido}
                onChange={(e) => setApellido(e.target.value)}
                placeholder="Apellido"
                required
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Email</label>
            <div className="control">
              <input
                type="email"
                className="input"
                value={Email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Telefono</label>
            <div className="control">
              <input
                type="number"
                className="input"
                value={Telefono}
                onChange={(e) => setTelefono(e.target.value)}
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Documento</label>
            <div className="control">
              <input
                type="number"
                className="input"
                value={Documento}
                onChange={(e) => setDocumento(e.target.value)}
                required
              />
            </div>
          </div>
          <div className="field">
          <label className="label" align='center'>Idioma de preferencia para la Visita Guiada</label>
          <div className="select is-fullwidth">
                <select
                    value={IdIdioma}                       
                    onChange={(e) => setIdIdiomas(e.target.value)}
                    key={Idiomas.IdIdioma}
                >
                <option value="0">Seleccionar</option>
                  {Idiomas.map((Idiomas) => (
                  
                  <option value={Idiomas.IdIdioma}>{Idiomas.Nombre}</option>
                
                  ))}
                </select>
          </div>
          </div>

          <div className="field">
            <button type="submit" className="button is-success">
              Save
            </button>
            <Link
                    to={'/listarArea'}
                    className="button is-small is-info mr-2"
                  >
                    Back
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default SeleccionarVisitaGuiada;
