import React, { useState, useEffect} from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
 
const CrearVisitaGuiada = () => {
  const [Nombre, setNombre] = useState("");
  const [IdIdioma, setIdIdioma] = useState("");
  const [IdVisitaGuiada, setIdVisitaGuiada] = useState("");
  const [IdTurno, setIdTurno] = useState("");
  const [IdArea1, setIdArea1] = useState("");
  const [IdArea2, setIdArea2] = useState("");
  const [IdArea3, setIdArea3] = useState("");
  const [Turnos, setTurnos] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    getTiposAreas();
  }, []);

  const CrearVisitaGuiada = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/visitasGuiadas", {
      Nombre,  
      });
      //
      await axios.post("http://localhost:5000/idiomasPorVisitasGuiadas", {
      IdIdioma,
      IdVisitaGuiada  
      });
      //
      await axios.post("http://localhost:5000/turnosPorVisitasGuiadas", {
      IdTurno,
      IdVisitaGuiada  
      });
      //
      await axios.post("http://localhost:5000/visitasGuiadasPorAreas", {
      IdArea1,
      IdArea2,
      IdArea3,
      IdVisitaGuiada  
      });
      
      navigate("/listarArea");
    } catch (error) {
      console.log(error);
    }
  };

  const getTurnos = async () => {
    const response = await axios.get("http://localhost:5000/turnos");
    setTurnos(response.data);
  };

  return (
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
      <form onSubmit={(event) => {CrearArea(event);}}>
        
          <div className="field">
            <label className="label">Nombre</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={Nombre}
                onChange={(e) => setNombre(e.target.value)}
                placeholder="Nombre"
                required
              />
            </div>
          </div>

          <div className="field">
          <label className="label">TipoArea</label>
          <div className="select is-fullwidth">
                <select
                    value={IdTurno}                       
                    onChange={(e) => setIdTurno(e.target.value)}
                    key={Turnos.IdTurno}
                >
                <option value="0">Seleccionar</option>
                  {Turnos.map((Turnos) => (
                  
                  <option value={Turnos.IdTurno}>{Turnos.FechaYHora}</option>
                
                  ))}
                </select>
          </div>
          </div>

          <div className="field">
            <label className="label">Descripcion</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={Descripcion}
                onChange={(e) => setDescripcion(e.target.value)}
                placeholder="Apellido"
                required="Por favor escriba una descripcion valido"
              />
            </div>
          </div>
          <div className="field">
            <button type="submit" className="button is-success">
              Save
            </button>
            <Link
                    to={'/listarArea'}
                    className="button is-small is-info mr-2"
                  >
                    Back
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CrearVisitaGuiada;
