import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams, Link} from "react-router-dom";

const EditPersona = () => {
  const [email, setEmail] = useState("");
  const [nombre, setNombre] = useState("");
  const [apellido, setApellido] = useState([]);
  const [edad, setEdad] = useState("");
  const [sexo, setSexo] = useState("");
  const [idTipoDocumento, setIdTipoDocumento] = useState("");
  const [tipoDocumento, setTipoDocumento] = useState([]);
  const { idPersona } = useParams();
  const navigate = useNavigate();


  
  useEffect(() => {
    getTipoDocumentos();
    getPersonaById();
  }, []);

  const updatePersona = async (e) => {
    e.preventDefault();
    try {
      await axios.patch("http://localhost:5000/persona/${idPersona}", {
      idTipoDocumento,
      email,  
      nombre,
      apellido,
      edad,
      sexo,
      });
      navigate("/");
    } catch (error) {
      console.log(error);
    }
  };

  const getTipoDocumentos = async () => {
    const response = await axios.get("http://localhost:5000/tipodocumentos");
    setTipoDocumento(response.data);
  };
  
  const getPersonaById = async () => {
    const response = await axios.get(`http://localhost:5000/persona/${idPersona}`);
    const i = 0;
    setEmail(response.data[i].email);
    setNombre(response.data[i].nombre);
    setApellido(response.data[i].apellido);
    setEdad(response.data[i].edad);
    setSexo(response.data[i].sexo);
    setTipoDocumento(response.data[i].tipoDocumento);
    console.log(JSON.stringify(response, null, 1));

  };

  //const volverPerfilList = "to={'/'}";

  return (
    <div className="columns mt-5 is-centered">
    <div className="column is-half">
    <form onSubmit={(event) => {updatePersona(event);}}>    
        <div className="field">
          <label className="label">Email</label>
          <div className="control">
            <input
              type="email"
              className="input"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Email"
              required
            />
          </div>
        </div>
        <div className="field">
          <label className="label">TipoDocumento</label>
          <div className="select is-fullwidth">
                <select
                    value={idTipoDocumento}                       
                    onChange={(e) => setIdTipoDocumento(e.target.value)}
                    key={tipoDocumento.id}
                >
                <option value="0">Seleccionar</option>
                  {tipoDocumento.map((tipoDocumento) => (
                  
                  <option value={tipoDocumento.idTipoDocumento}>{tipoDocumento.TipoDocumento}</option>
                
                  ))}
                </select>
          </div>
          </div>
        <div className="field">
          <label className="label">Nombre</label>
          <div className="control">
            <input
              type="text"
              pattern="[a-z A-Z ñ]{2,20}"
              className="input"
              value={nombre}
              onChange={(e) => setNombre(e.target.value)}
              placeholder="Nombre"
              required
            />
          </div>
        </div>
        <div className="field">
          <label className="label">Apellido</label>
          <div className="control">
            <input
              type="text"
              className="input"
              pattern="[a-z A-Z ñ]{2,20}"
              value={apellido}
              onChange={(e) => setApellido(e.target.value)}
              placeholder="Apellido"
              required="Por favor escriba un nombre valido"
            />
          </div>
        </div>
        <div className="field">
          <label className="label">Edad</label>
          <div className="control">
            <input
              type="number"
              pattern="[1-120] {3}"
              className="input"
              value={edad}
              onChange={(e) => setEdad(e.target.value)}
              placeholder="edad"
              required
            />
          </div>
        </div>
        <div className="field">
          <label className="label">Sexo</label>
          <div className="control">
            <div className="select is-fullwidth">
            <select 
            value={sexo}
            onChange={(e) => setSexo(e.target.value)}
            placeholder="Seleccionar"
            required>
        
            <option value={"m"}> M </option>
            <option value={"f"}> F </option>
            <option value={"x"}> X </option>
            </select>
            </div>
          </div>
        </div>
        <div className="field">
          <button type="submit" className="button is-success">
            Save
          </button>
          <Link
                  to={'/'}
                  className="button is-small is-info mr-2"
                >
                  Back
          </Link>
        </div>
      </form>
    </div>
  </div>
);
};
export default EditPersona;
