import React, { useState, useEffect} from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
 
const CrearObra = () => {
  const [Autor, setAutor] = useState("");
  const [Descripcion, setDescripcion] = useState("");
  const [Nombre, setNombre] = useState("");
  const [IdTipoObra, setIdTipoObra] = useState("");
  const [IdArea, setIdArea] = useState("");
  const [TiposObras, setTiposObras] = useState([]);
  const navigate = useNavigate();
  const [CantidadAreas, setCantidadAreas] = useState("");
  const [CantidadSelects, setCantidadSelects] = useState([]);
    let cantidadareas = 1;
  useEffect(() => {
    getTiposObras();
  }, []);

  function aumentarArreglo(){
    cantidadareas+=1;
  }

  const getTiposObras = async () => {
    const response = await axios.get("http://localhost:5000/tiposobras");
    setTiposObras(response.data);
  };

  return (
<body>
    <div className="columns mt-5 is-centered">
    <div className="column is-half">
    <form onSubmit={(event) => {CrearObra(event);}}>    
        
        <div className="field">
          <label className="label">Autor</label>
          <div className="control">
            <input
              type="text"
              className="input"
              value={Autor}
              onChange={(e) => setAutor(e.target.value)}
              placeholder="Autor"
              required
            />
          </div>
        </div>
        <div className="field">
          <label className="label">Nombre de la Obra</label>
          <div className="control">
            <input
              type="text"
              className="input"
              value={Nombre}
              onChange={(e) => setNombre(e.target.value)}
              placeholder="Autor"
              required
            />
          </div>
        </div>
        <div className="field">
          <label className="label">Descripcion</label>
          <div className="control">
            <input
              type="text"
              pattern="[a-z A-Z ñ]{2,20}"
              className="input"
              value={Descripcion}
              onChange={(e) => setDescripcion(e.target.value)}
              placeholder="Descripcion"
              required
            />
          </div>
        </div>
      <button onClick={aumentarArreglo()}>Agregar area</button>

          <div className="field">
                {CantidadSelects.map(() => (
                        <select
                            value={IdTipoObra}                       
                            onChange={(e) => setIdTipoObra(e.target.value)}
                            key={TiposObras.IdTipoObra}
                        >
                            
                                <option value={TiposObras.IdTipoObra}>{TiposObras.IdTipoObra}</option>
                           
                        </select>
               
                    ))}
          </div>

        <div className="field">
          <button type="submit" className="button is-success">
            Save
          </button>
          <Link
                  to={'/ListarObra'}
                  className="button is-small is-info mr-2"
                >
                  Back
          </Link>
        </div>
      </form>
    </div>
  </div>
</body>
  );
};

export default CrearObra;
