import React, { useState, useEffect} from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
 
const AddPersona = () => {
  //
  const [email, setEmail] = useState("");
  const [nombre, setNombre] = useState("");
  const [apellido, setApellido] = useState([]);
  const [edad, setEdad] = useState("");
  const [sexo, setSexo] = useState("");
  const navigate = useNavigate();
  const[currentPage, setCurrentPage] = useState(0)

  const filteredPersona = (): Persona[] => {
    return persona.slice(currentPage, currentPage + 5);
  } 

  
  const nextPage = () => {
    setCurrentPage (currentPage + 5);
  } 

  const prevPage = () => {
    setCurrentPage (currentPage + 5);
  }

  const createPersona = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/persona", {
      email,  
      nombre,
      apellido,
      edad,
      sexo,
      });
      navigate("/");
    } catch (error) {
      console.log(error);
    }
  };
  return (
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
      <form onSubmit={(event) => {createPersona(event);}}>
          <div className="field">
            <label className="label">Email</label>
            <div className="control">
              <input
                type="email"
                className="input"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Email"
                required
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Nombre</label>
            <div className="control">
              <input
                type="text"
                pattern="[a-z A-Z ñ]{2,20}"
                className="input"
                value={nombre}
                onChange={(e) => setNombre(e.target.value)}
                placeholder="Nombre"
                required
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Apellido</label>
            <div className="control">
              <input
                type="text"
                className="input"
                pattern="[a-z A-Z ñ]{2,20}"
                value={apellido}
                onChange={(e) => setApellido(e.target.value)}
                placeholder="Apellido"
                required="Por favor escriba un nombre valido"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Edad</label>
            <div className="control">
              <input
                type="number"
                pattern="[1-120] {3}"
                className="input"
                value={edad}
                onChange={(e) => setEdad(e.target.value)}
                placeholder="edad"
                required
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Sexo</label>
            <div className="control">
              <div className="select is-fullwidth">
              <select 
              value={sexo}
              onChange={(e) => setSexo(e.target.value)}
              placeholder="Seleccionar"
              required>
          
              <option value={"m"}> M </option>
              <option value={"f"}> F </option>
              <option value={"x"}> X </option>
              </select>
              </div>
            </div>
          </div>
          <div className="field">
            <button type="submit" className="button is-success">
              Save
            </button>
            <Link
                    to={'/'}
                    className="button is-small is-info mr-2"
                  >
                    Back
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddPersona;
