import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const ListPersona = () => {
  const [persona, setPersona] = useState([]);

  useEffect(() => {
    getPersona();
  }, []);

  const getPersona = async () => {
    const response = await axios.get("http://localhost:5000/persona");
    setPersona(response.data);
  };

  return (
    <body>
    <header>
    <img className="img scroll is-variable" src="C:\xampp\htdocs\vec2\frontend\src\img\VEC-COVD-300x300.png"></img>
    </header>
    <div className="">
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
        <Link to={`add`} className="button is-success">
          Add Persona
        </Link>
        
        <table className="table is-striped is-fullwidth">
          <thead>
            <tr>
              <th>No</th>
              <th>email</th>
              <th>nombre</th>
              <th>apellido</th>
              <th>edad</th>
              <th>sexo</th>
              <th>actions</th>
            </tr>
          </thead>
          <tbody>
            {persona.map((persona) => (
              <tr key={persona.idPersona}>
                <td>{persona.idPersona}</td>
                <td>{persona.email}</td>
                <td>{persona.nombre}</td>
                <td>{persona.apellido}</td>                
                <td>{persona.edad}</td>
                <td>{persona.sexo}</td>
                <td>
                <Link
                    to={`edit/${persona.idPersona}`}
                    
                    className="button is-small is-info mr-2 icon-pencil"
                    title="Editar"
                  >
                    edit
                  </Link>
                </td>
              </tr>
            ))}
            
          </tbody>
        </table>
    </div>
    </div>
    </div>
    </body>
  );
};

export default ListPersona;
