import React, { useState, useEffect} from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
 
const CrearVisitaGuiada = () => {
  const [Guia, setGuia] = useState("");
  const [Descripcion, setDescripcion] = useState("");
  const [Nombre, setNombre] = useState("");
  const [IdTipoObra, setIdTipoObra] = useState("");
  const [IdArea, setIdArea] = useState("");
  const [TiposObras, setTiposObras] = useState([]);
  const navigate = useNavigate();
  const [CantidadAreas, setCantidadAreas] = useState([]);
  const [CantidadSelects, setCantidadSelects] = useState("");

  useEffect(() => {
    getTiposObras();
  }, []);
let newList;
  function llenarArreglo(){
        let newList = [];
    for (let i = 1; i <= CantidadSelects; i++) {
       return(
        <select>
          
        </select>
       
    )}
    setCantidadAreas(newList);
  }

  const CrearVisitaGuiada = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/areas", {
      Guia,
      Descripcion,  
      Nombre,
      IdTipoObra,
      IdArea,
      });
      navigate("/listarObra");
    } catch (error) {
      console.log(error);
    }
  };

  const getTiposObras = async () => {
    const response = await axios.get("http://localhost:5000/tiposobras");
    setTiposObras(response.data);
  };

  return (
<body>
    <div className="columns mt-5 is-centered">
    <div className="column is-half">
    <form onSubmit={(event) => {CrearVisitaGuiada(event);}}> 
       
    <input
              type="checkbox"
              onChange={(e) => {
                if (e.target.checked) {
                  newList.push();
             
                  setCantidadAreas(newList);
                  console.log(newList);
                }
              }}
            />
       
        <div className="field">
          <label className="label">areas a recorrer por la visita guiada</label>
          <div className="select is-fullwidth">
                <select
                    value={CantidadSelects}                        
                    onChange={(e) => {setCantidadSelects(e.target.value); llenarArreglo()}}
                    key={TiposObras.IdTipoObra}
                >
                  <option value={""}>Seleccionar</option>
                  {TiposObras.map((TiposObras) => (
                  
                  <option value={TiposObras.IdTipoObra}>{TiposObras.Nombre}</option>
                
                  ))}
                </select>
            </div>
          </div>
                   {llenarArreglo()}
          <div className="field">
                {
                    CantidadAreas.map(()=>
                                <div cla ssName="select is-fullwidth">
                                    <select
                                        value={IdTipoObra}                       
                                        onChange={(e) => setIdTipoObra(e.target.value)}
                                        key={TiposObras.IdTipoObra}
                                      
                                    >
                                        <option value={""}>Seleccionar</option>
                                        {TiposObras.map((TiposObras) => (
                                      
                                        <option value={TiposObras.IdTipoObra}>{TiposObras.IdTipoObra}</option>
                                        
                                        ))}
                                    </select>
                                </div>
                    )
                }
          </div>

        <div className="field">
          <button type="submit" className="button is-success">
            Save
          </button>
          <Link
                  to={'/ListarObra'}
                  className="button is-small is-info mr-2"
                >
                  Back
          </Link>
        </div>
      </form>
    </div>
  </div>
</body>
  );
};

export default CrearVisitaGuiada;
