import React, { useState, useEffect} from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
 
const CrearVisitaGuiada = () => {
  const [Nombre, setNombre] = useState("");
  const [FechaYHora, setFechaYHora] = useState("");
  const [IdIdioma, setIdIdioma] = useState("");
  const [IdVisitaGuiada, setIdVisitaGuiada] = useState("");
  const [IdArea1, setIdArea1] = useState("");
  const [IdArea2, setIdArea2] = useState("");
  const [IdArea3, setIdArea3] = useState("");
  const [Areas, setAreas] = useState([]);
  const [Idiomas, setIdiomas] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    getAreas();
    getIdiomas();
  }, []);

  const CrearVisitaGuiada = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/visitasGuiadas", {
      Nombre,
      FechaYHora 
      });
      //
      await axios.post("http://localhost:5000/idiomasPorVisitasGuiadas", {
      IdIdioma,
      IdVisitaGuiada  
      });
      //
      await axios.post("http://localhost:5000/visitasGuiadasPorAreas", {
      IdArea1,
      IdArea2,
      IdArea3,
      IdVisitaGuiada  
      });
      
      navigate("/listarArea");
    } catch (error) {
      console.log(error);
    }
  };

  const getAreas = async () => {
    const response = await axios.get("http://localhost:5000/areas");
    setAreas(response.data);
  };
  const getIdiomas = async () => {
    const response = await axios.get("http://localhost:5000/idiomas");
    setIdiomas(response.data);
  };

  return (
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
      <form onSubmit={(event) => {CrearVisitaGuiada(event);}}>
        
          <div className="field">
            <label className="label">Nombre visita guiada</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={Nombre}
                onChange={(e) => setNombre(e.target.value)}
                placeholder="Nombre"
                required
              />
            </div>
          </div>

         
          <div className="field">
            <label className="label">Fecha y hora</label>
            <div className="control">
              <input
                type="datetime"
                className="input"
                value={FechaYHora}
                onChange={(e) => setFechaYHora(e.target.value)}
                required
              />
            </div>
          </div>
          
          <div className="field">
          <label className="label">Area 1 a visitar</label>
          <div className="select is-fullwidth">
                <select
                    value={IdArea1}                       
                    onChange={(e) => setIdArea1(e.target.value)}
                    key={Areas.IdArea}
                >
                <option value="0">Seleccionar</option>
                  {Areas.map((Areas) => (
                  
                  <option value={Areas.IdArea}>{Areas.Nombre}</option>
                
                  ))}
                </select>
          </div>
          </div>
          <div className="field">
          <label className="label">Area 2 a visitar</label>
          <div className="select is-fullwidth">
                <select
                    value={IdArea2}                       
                    onChange={(e) => setIdArea2(e.target.value)}
                    key={Areas.IdArea}
                >
                <option value="0">Seleccionar</option>
                  {Areas.map((Areas) => (
                  
                  <option value={Areas.IdArea}>{Areas.Nombre}</option>
                
                  ))}
                </select>
          </div>
          </div>
          <div className="field">
          <label className="label">Area 3 a visitar</label>
          <div className="select is-fullwidth">
                <select
                    value={IdArea3}                       
                    onChange={(e) => setIdArea3(e.target.value)}
                    key={Areas.IdArea}
                >
                <option value="0">Seleccionar</option>
                  {Areas.map((Areas) => (
                  
                  <option value={Areas.IdArea}>{Areas.Nombre}</option>
                
                  ))}
                </select>
          </div>
          </div>
          <div className="field">
          <label className="label">Idioma de la visita guiada</label>
          <div className="select is-fullwidth">
                <select
                    value={IdIdioma}                       
                    onChange={(e) => setIdIdioma(e.target.value)}
                    key={Idiomas.IdIdioma}
                >
                <option value="0">Seleccionar</option>
                  {Idiomas.map((Idiomas) => (
                  
                  <option value={Idiomas.IdIdioma}>{Idiomas.Nombre}</option>
                
                  ))}
                </select>
          </div>
          </div>
          <div className="field">
            <button type="submit" className="button is-success">
              Save
            </button>
            <Link
                    to={'/listarArea'}
                    className="button is-small is-info mr-2"
                  >
                    Back
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CrearVisitaGuiada;
