import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams, Link} from "react-router-dom";

const EditPersona = () => {
  //
  //const [nombre_estado, setter_estado] = useState(inicializacion de tipo de dato y valor);
  const [nombre, setNombre] = useState("");
  const [apellido, setApellido] = useState("");
  const [email, setEmail] = useState("");
  const [edad, setEdad] = useState("");
  const [sexo, setSexo] = useState("");


  //
  const navigate = useNavigate();
  const { idPersona} = useParams();
  //const { seccion } = useParams();

  useEffect(() => {
    getPersonaById();
  }, []);

  const updatePersona = async (e) => {
    e.preventDefault();
    try {
      await axios.patch(`http://localhost:5000/perfiles/${idPersona}`, {
        nombre,
        
      });
      navigate("/");
    } catch (error) {
      console.log(error);
    }
  };

  //const getUserById = async (id) => { ESTA MAL
  const getPersonaById = async () => {
    const response = await axios.get(`http://localhost:5000/perfiles/${idPersona}`);
    //
    //SELECT `id` FROM `users` ORDER BY `id` DESC LIMIT 1
    //var i, max = 1000;//Modificar por un max según cantidad de registros en la tabla.
    //for(i = 0; i < max; i++){
    const i = 0; //Porque solo leemos un único registro.
      
    setNombre(response.data[i].nombre);

    
    //
    console.log(JSON.stringify(response, null, 1));
    //}
    //
    //console.log("ID por parámetro: "+id);
    //Desde el inspector de chrome/consola.
  };

  const volverPersonaList = "to={'/'}";

  return (
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
        <form onSubmit={updatePersona}>
          <div className="field">
            <label className="label">Nombre del Perfil</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={nombre}
                onChange={(e) => setNombre(e.target.value)}
                placeholder="nombre"
                required
              />
            </div>
          </div>
          <div className="field">
            <label className="label">descripcion</label>
            <div className="control">
              <input
                type="descripcion"
                className="input"
                value={descripcion}
                onChange={(e) => setDescripcion(e.target.value)}
                placeholder="descripcion"
                required
              />
            </div>
          </div>
          <div className="field">
            <button type="submit" className="button is-success">
              Update
            </button>
            {/*
            <Link
                    {  == 1 ? volverUserList : volverDownUserList}
                    className="button is-small is-info mr-2"
                  >
                    Back
            </Link>
            */}
            {/**/}
            <Link
                    to={'/'}
                    className="button is-small is-info mr-2"
                  >
                    Back2
            </Link>
            {/**/}
            {/*
            <Link
                    to={'/downUsers'}
                    className="button is-small is-info mr-2"
                  >
                    Back2
            </Link>
            */}
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditPerfil;
