import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import DataTable from 'react-data-table-component';



const ListPersona = () => {
  const [persona, setPersona] = useState([]);
  /*const [personaSearch, setPersonaSearch] = useState([]);*/
  let[currentPage, setCurrentPage] = useState(0);
  const [ search, setSearch ] = useState("")
  const [pending, setPending] = React.useState(true);
	const [rows, setRows] = React.useState([]);
  let i;

  useEffect(() => {
    getPersona();
   // getPersonaSearch();
  }, []);

  const searcher = (e) => {
    setSearch(e.target.value)   
  }

    const nextPage = () => {
    if(currentPage<=persona.length)
    currentPage =currentPage+i;
    setCurrentPage (currentPage);
    getPersona(currentPage)
    console.log(currentPage)
  } 

  const prevPage = () => {
   if(currentPage>0)
    currentPage =currentPage-i;
    setCurrentPage (currentPage);
    getPersona(currentPage)
    console.log(currentPage)
  }

 /* const getPersonaSearch = async () => {
    const response = await axios.get("http://localhost:5000/persona");
    setPersonaSearch(response.data);
  };
*/

  const getPersona = async () => {
     const response = await axios.get("http://localhost:5000/persona");
     //setPersona(response.data.slice(currentPage, currentPage+i));
     setPersona(response.data);
   };
/*
   const results = !search ? personaSearch : personaSearch.filter((dato)=> dato.nombre.toLowerCase().includes(search.toLocaleLowerCase()))
   results.slice(currentPage, currentPage+i);
*/

React.useEffect(() => {
  const timeout = setTimeout(() => {
    setRows(persona);
    setPending(false);
  }, 2000);
  return () => clearTimeout(timeout);
}, []);

const columnas = [
  {
    name : 'idPersona',
    selector : 'idPersona',
    sortable : true
  },
  {
    name : 'email',
    selector : 'email',
    sortable : true
  },
  {
    name : 'nombre',
    selector : 'nombre',
    sortable : true
  },
  {
    name : 'apellido',
    selector : 'apellido',
    sortable : true
  },
  {
    name : 'edad',
    selector : 'edad',
    sortable : true
  },
  {
    name : 'sexo',
    selector : 'sexo',
    sortable : true
  }
]

  return (         
<body>
    <div>
    <Link to={`add`} className="button is-success">
          Add Persona
    </Link>
      <DataTable
      pagination
      progressPending={pending}

      columns={columnas}
      data={persona} 
       />
    </div>
</body>

  );
};


export default ListPersona;
